import React, {useEffect} from 'react';
import { Html5QrcodeScanner } from 'html5-qrcode';
import axios from 'axios';

export default function StudentScan({user}){
  useEffect(()=>{
    const scanner = new Html5QrcodeScanner('reader',{ fps:10, qrbox:250 });
    scanner.render(async (decodedText)=>{
      const qr_code = decodedText;
      navigator.geolocation.getCurrentPosition(async (pos)=>{
        const {latitude, longitude} = pos.coords;
        const res = await axios.post('http://localhost:5000/api/attendance',{student_id: user.id, qr_code, latitude, longitude});
        alert(JSON.stringify(res.data));
      }, ()=> alert('Enable GPS to mark attendance'));
    });
  },[user]);
  return (
    <div style={{padding:20}}>
      <h2>Student: {user.name}</h2>
      <div id="reader"></div>
    </div>
  )
}
