import React, { useEffect, useRef, useState } from 'react';
import { Html5Qrcode, Html5QrcodeSupportedFormats } from 'html5-qrcode';
import axios from 'axios';
import { generateDeviceFingerprint } from '../utils/deviceFingerprint';
import './QRScanner.css';
import { API_BASE } from '../config';

export default function QRScanner({ user }) {
  const html5QrCodeRef = useRef(null);
  const [error, setError] = useState(null);
  const [isRetrying, setIsRetrying] = useState(false);
  const [isScanning, setIsScanning] = useState(false);

  useEffect(() => {
    let isMounted = true;
    
    const handleCameraError = (errorMessage) => {
      if (!isMounted) return;
      
      let userFriendlyMessage = "Camera error: ";
      
      if (errorMessage.includes("NotReadableError") || errorMessage.includes("Could not start video source")) {
        userFriendlyMessage += "Camera is already in use by another app. Please close other apps using the camera and try again.";
      } else if (errorMessage.includes("Permission denied") || errorMessage.includes("NotAllowedError")) {
        userFriendlyMessage += "Camera permission was denied. Please allow camera access in your browser settings and refresh the page.";
      } else if (errorMessage.includes("NotFoundError") || errorMessage.includes("no camera")) {
        userFriendlyMessage += "No camera found on this device.";
      } else {
        userFriendlyMessage += errorMessage;
      }
      
      setError(userFriendlyMessage);
      setIsScanning(false);
    };

    const handleScanSuccess = async (token, scanner) => {
      if (!isMounted) return;
      
      try {
        // Stop scanning temporarily
        await scanner.stop();
        setIsScanning(false);

        navigator.geolocation.getCurrentPosition(
          async (pos) => {
            if (!isMounted) return;
            
            const { latitude, longitude } = pos.coords;
            const device_fingerprint = generateDeviceFingerprint();

            try {
              const res = await axios.post(`${API_BASE}/api/attendance-token`, {
                student_id: user.id,
                token,
                latitude,
                longitude,
                device_fingerprint
              });

              if (res.data.error) {
                alert("Error: " + res.data.error);
              } else {
                alert(res.data.message || "Attendance marked successfully!");
              }
              
              // Restart scanner
              if (isMounted) {
                startScanner();
              }
            } catch (err) {
              alert("Failed to mark attendance: " + (err.response?.data?.error || err.message));
              // Restart scanner
              if (isMounted) {
                startScanner();
              }
            }
          },
          () => {
            alert("Enable GPS to mark attendance.");
            // Restart scanner
            if (isMounted) {
              startScanner();
            }
          }
        );
      } catch (e) {
        console.error("Error handling scan:", e);
        if (isMounted) {
          startScanner();
        }
      }
    };

    const startScanner = async () => {
      if (!isMounted) return;
      
      // Clear any previous error
      setError(null);

      // Check if getUserMedia is supported
      if (!navigator.mediaDevices || !navigator.mediaDevices.getUserMedia) {
        setError("Camera access is not supported in this browser. Please use a modern browser like Chrome, Firefox, or Safari.");
        return;
      }

      // Check if we're on HTTPS or localhost (required for camera access)
      const isSecureContext = window.isSecureContext || 
        window.location.protocol === 'https:' || 
        window.location.hostname === 'localhost' || 
        window.location.hostname === '127.0.0.1' ||
        window.location.hostname.startsWith('192.168.') ||
        window.location.hostname.startsWith('10.') ||
        window.location.hostname.startsWith('172.');

      if (!isSecureContext) {
        setError("Camera access requires HTTPS. Please access this site via HTTPS or use a local network address.");
        return;
      }

      // Clean up any existing scanner
      if (html5QrCodeRef.current) {
        try {
          await html5QrCodeRef.current.stop().catch(() => {});
          await html5QrCodeRef.current.clear().catch(() => {});
        } catch (e) {
          console.log("Cleaning up previous scanner:", e);
        }
      }

      const qrboxSize = window.innerWidth < 480 ? 220 : 280;
      const html5QrCode = new Html5Qrcode("reader");
      html5QrCodeRef.current = html5QrCode;

      try {
        // Request camera permission first with better error handling
        let cameraId = null;
        try {
          const devices = await Html5Qrcode.getCameras();
          if (devices && devices.length > 0) {
            // Prefer back camera (environment) on mobile
            const backCamera = devices.find(device => 
              device.label.toLowerCase().includes('back') || 
              device.label.toLowerCase().includes('rear') ||
              device.label.toLowerCase().includes('environment')
            );
            cameraId = backCamera ? backCamera.id : devices[0].id;
          }
        } catch (e) {
          console.log("Could not enumerate cameras, using default:", e);
        }

        // Mobile-friendly camera configuration
        const config = {
          fps: 10,
          qrbox: { width: qrboxSize, height: qrboxSize },
          aspectRatio: 1.0,
          formatsToSupport: [Html5QrcodeSupportedFormats.QR_CODE]
        };

        await html5QrCode.start(
          cameraId || { facingMode: "environment" },
          config,
          (decodedText) => {
            // Success callback
            if (isMounted) {
              const token = decodedText;
              handleScanSuccess(token, html5QrCode);
            }
          },
          (errorMessage) => {
            // Error callback - ignore scanning errors, only log camera errors
            if (errorMessage.includes("NotReadableError") || 
                errorMessage.includes("Could not start video source") ||
                errorMessage.includes("Permission denied") ||
                errorMessage.includes("NotFoundError") ||
                errorMessage.includes("NotAllowedError")) {
              if (isMounted) {
                handleCameraError(errorMessage);
              }
            }
          }
        );
        
        if (isMounted) {
          setIsScanning(true);
        }
      } catch (err) {
        if (isMounted) {
          handleCameraError(err.message || err.toString());
        }
      }
    };

    startScanner();

    return () => {
      isMounted = false;
      const cleanup = async () => {
        if (html5QrCodeRef.current) {
          try {
            await html5QrCodeRef.current.stop().catch(() => {});
            await html5QrCodeRef.current.clear().catch(() => {});
          } catch (e) {
            console.log("Error cleaning up scanner:", e);
          }
        }
      };
      cleanup();
    };
  }, [user, isRetrying]);

  const handleRetry = async () => {
    setError(null);
    setIsRetrying(!isRetrying);
    
    // Force cleanup before retry
    if (html5QrCodeRef.current) {
      try {
        await html5QrCodeRef.current.stop().catch(() => {});
        await html5QrCodeRef.current.clear().catch(() => {});
      } catch (e) {
        console.log("Cleanup on retry:", e);
      }
    }
    
    // Small delay to ensure cleanup is complete
    setTimeout(() => {
      setIsRetrying(prev => !prev);
    }, 500);
  };

  return (
    <div className="qr-scanner-container">
      <div className="qr-scanner-header">
        <h2>Scan QR Code</h2>
        <p>
          Point your camera at the QR code displayed in class. Your location and device information will be verified.
        </p>
      </div>

      {error && (
        <div style={{
          backgroundColor: '#fee',
          border: '1px solid #fcc',
          borderRadius: '8px',
          padding: '15px',
          margin: '15px 0',
          color: '#c33'
        }}>
          <strong>Error:</strong> {error}
          <button 
            onClick={handleRetry}
            style={{
              display: 'block',
              marginTop: '10px',
              padding: '8px 16px',
              backgroundColor: '#4CAF50',
              color: 'white',
              border: 'none',
              borderRadius: '4px',
              cursor: 'pointer',
              fontSize: '14px'
            }}
          >
            Retry Camera
          </button>
        </div>
      )}

      <div className="qr-reader-wrapper">
        <div id="reader"></div>
      </div>
    </div>
  );
}
