import React, { useEffect, useRef } from 'react';
import { Html5QrcodeScanner } from 'html5-qrcode';
import axios from 'axios';

export default function QRScanner({ user }) {
  const scannerRef = useRef(null);

  useEffect(() => {
    if (!scannerRef.current) return;

    const scanner = new Html5QrcodeScanner(
      'reader',
      { fps: 10, qrbox: 250 },
      false
    );

    scanner.render(
      async (decodedText) => {
        const qr_code = decodedText;
        try {
          navigator.geolocation.getCurrentPosition(
            async (pos) => {
              const { latitude, longitude } = pos.coords;
              const res = await axios.post('http://localhost:5000/api/attendance', {
                student_id: user.id,
                qr_code,
                latitude,
                longitude
              });
              alert('Attendance marked successfully!\n' + JSON.stringify(res.data));
              // Stop scanner after successful scan
              scanner.clear();
            },
            () => {
              alert('Enable GPS to mark attendance');
            }
          );
        } catch (err) {
          alert('Failed to mark attendance: ' + err.message);
        }
      },
      (errorMessage) => {
        // Handle scan errors silently or log them
      }
    );

    scannerRef.current = scanner;

    return () => {
      if (scannerRef.current) {
        scannerRef.current.clear();
      }
    };
  }, [user]);

  return (
    <div style={{ textAlign: 'center', padding: '20px' }}>
      <h2>Scan QR Code</h2>
      <p style={{ color: '#666', marginBottom: '20px' }}>
        Place the camera over the QR code displayed in class
      </p>
      <div id="reader" style={{ display: 'inline-block' }}></div>
    </div>
  );
}
