import React, { useState } from 'react';
import axios from 'axios';
import QRCode from 'react-qr-code';
import './Dashboard.css';

export default function Dashboard({ user, setUser }) {
  const [qrCode, setQrCode] = useState('');
  const [showQR, setShowQR] = useState(false);
  const [activeView, setActiveView] = useState('dashboard');

  const logout = () => {
    setUser(null);
  };

  const generateQR = async () => {
    try {
      const code = `CLASS-${user.id}-${Date.now()}`;
      await axios.post('http://localhost:5000/api/create-session', {
        class_id: 1,
        qr_code: code,
        teacher_id: user.id
      });
      setQrCode(code);
      setShowQR(true);
    } catch (err) {
      alert('Failed to generate QR code');
    }
  };

  const renderDashboardContent = () => {
    if (activeView === 'qr' && user.role === 'teacher') {
      return (
        <div className="feature-content">
          <h2>Generate QR Code</h2>
          <button onClick={generateQR} className="action-btn">
            Generate New QR Code
          </button>
          {showQR && (
            <div className="qr-display">
              <h3>Show this on Projector</h3>
              <div className="qr-code-container">
                <QRCode value={qrCode} size={256} />
              </div>
              <p className="qr-text">{qrCode}</p>
            </div>
          )}
        </div>
      );
    }

    if (activeView === 'scan' && user.role === 'student') {
      return (
        <div className="feature-content">
          <h2>Scan QR Code</h2>
          <p>Place the camera over the QR code displayed in class</p>
          <div id="reader"></div>
        </div>
      );
    }

    if (activeView === 'attendance' && user.role === 'student') {
      return (
        <div className="feature-content">
          <h2>My Attendance</h2>
          <p>View your attendance records</p>
        </div>
      );
    }

    return (
      <div className="dashboard-welcome">
        <h2>Welcome to Attendance Management System</h2>
        <p>Select an option from the menu to get started</p>
      </div>
    );
  };

  return (
    <div className="dashboard-container">
      <header className="dashboard-header">
        <div className="header-left">
          <div className="logo">JSSATE BENGALURU</div>
        </div>
        <div className="header-right">
          <div className="user-profile">
            <div className="profile-pic">
              {user.name.charAt(0).toUpperCase()}
            </div>
            <div className="profile-info">
              <div className="profile-name">{user.name}</div>
              {user.role === 'student' && (
                <>
                  <div className="profile-id">{user.student_id || 'Student ID'}</div>
                  <div className="profile-course">{user.course || 'Course'}</div>
                  <div className="profile-section">{user.section || 'Section'}</div>
                </>
              )}
              {user.role === 'teacher' && (
                <div className="profile-role">Teacher</div>
              )}
            </div>
            <button onClick={logout} className="logout-btn">Logout</button>
          </div>
        </div>
      </header>

      <div className="dashboard-divider"></div>

      <main className="dashboard-main">
        <div className="dashboard-grid">
          {user.role === 'student' ? (
            <>
              <button 
                className="dashboard-btn btn-1" 
                onClick={() => setActiveView('scan')}
              >
                SCAN QR CODE
              </button>
              <button 
                className="dashboard-btn btn-2" 
                onClick={() => setActiveView('attendance')}
              >
                MY ATTENDANCE
              </button>
              <button 
                className="dashboard-btn btn-3" 
                onClick={() => setActiveView('dashboard')}
              >
                DASHBOARD
              </button>
            </>
          ) : (
            <>
              <button 
                className="dashboard-btn btn-1" 
                onClick={() => setActiveView('qr')}
              >
                GENERATE QR
              </button>
              <button 
                className="dashboard-btn btn-2" 
                onClick={() => setActiveView('report')}
              >
                VIEW REPORTS
              </button>
              <button 
                className="dashboard-btn btn-3" 
                onClick={() => setActiveView('dashboard')}
              >
                DASHBOARD
              </button>
            </>
          )}
        </div>

        <div className="main-content">
          {renderDashboardContent()}
        </div>
      </main>

      <footer className="dashboard-footer">
        <div className="footer-content">
          <div className="footer-left">
            Â© Copyright 2025. All Rights Reserved
          </div>
          <div className="footer-right">
            admissions@system.com | 080-28611702
          </div>
        </div>
      </footer>
    </div>
  );
}
